    <img className="img-fluid bottom" src={require('./img/yianni-cam.jpg')} alt="wrestler" />
     <p className="headline">{space}</p></a>
     {/* <img className="card-img-bottom" src={ball} alt="tennis ball" /> */}
     </div>
        </div>  



    );
  };
}

export default Story;
